import React, { Component} from 'react';
import NavBar from './NavBar';
import PropTypes from 'prop-types';
import axios from 'axios';
import './FeaturePage.css';

class FeaturePage extends Component {
    state = {
        book: {},
        isLoading: false,
    }

    static propTypes = {
        feature: PropTypes.object.isRequired
    }


    componentDidMount(){
        axios.get(`/wp-json/wp/v2/feature/${this.props.match.params.id}`)
            .then( res => {
                this.setState({
                book: res.data,
                isLoading: true
            })
            console.log(this.props)
        })
            .catch(err => console.log(err))

        const { featured_media } = this.props.feature;

        axios.get(`/wp-json/wp/v2/media/${featured_media}`)   
           .then( res => {
                this.setState({
                    imageURL: res[0].data.media_details.sizes.full.source_url,
        })
        console.log(this.props)
    })
}



    render(){
        const { book, imageURL, isLoading } = this.state;


        if(isLoading){
            return(

                <div>
                    <NavBar />
                    <img className="FeatureBlog-img" src={ imageURL } alt={book.title.render} />
                    <div className='FeaturePage-blurb' dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>

                </div>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default FeaturePage
