import React, { Component} from 'react';
import PropTypes from 'prop-types';
import NavBar from './NavBar';
import axios from 'axios';
import './FeaturePage.css';

class FeaturePage extends Component {
    state = {
        book: {},
        isLoading: false,
    }

    static propTypes = {
        feature: PropTypes.object.isRequired
    }


    componentDidMount(){
        const { featured_media, author } = this.props.feature;
        const getBook = axios.get(`/wp-json/wp/v2/feature/${this.props.match.params.id}`)
        const getImageURL = axios.get(`/wp-json/wp/v2/media/${featured_media}`)
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`);

        Promise.all([getImageURL, getBook, getAuthor]).then(res => {

            this.setState({
                book: res.data,
                imageURL: res[0].data.media_details.sizes.full.source_url,
                isLoading: true
            })
        })
}



    render(){
        const { book, imageURL, isLoading } = this.state;


        if(isLoading){
            return(

                <div>
                    <NavBar />
                    <img className="FeatureBlog-img" src={ imageURL } alt={book.title.render} />
                    <div className='FeaturePage-blurb' dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>

                </div>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default FeaturePage
