import React, { Component} from 'react';
import NavBar from './NavBar';
import axios from 'axios';

class FeaturePage extends Component {
    state = {
        book: {},
        isLoading: false,
        imageURL: ''
    }


    componentDidMount(){
        axios.get(`/wp-json/wp/v2/feature/${this.props.match.params.id}`)
            .then( res => {
                this.setState({
                book: res.data,
                isLoading: true
            })
        })
            .catch(err => console.log(err)) 
    }

    componentDidMount(){
        const getImageURL = axios.get(`/wp-json/wp/v2/media/${book.featured_media}`)
        Promise.all([getImageURL, getAuthor]).then(res => {

            this.setState({
                imageURL: res[0].data.media_details.sizes.full.source_url,
                isLoading: true
            })
        })
    }



    render(){
        const { book, isLoading } = this.state;

        if(isLoading){
            return(

                <div>
                    <NavBar />
                    <h1>{book.title.rendered}</h1>
                    <div dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>
                    <h4> Publisher: {book.acf.publisher}</h4>
                </div>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default FeaturePage
