import React, { Component } from 'react';
import axios from 'axios';
import ArticuleImageItem from './ArticuleImageItem';

class ArticuleImage extends Component {
    constructor(props){
        super(props)
        this.state = {
            books: [],
            isLoaded: false
        }
    }

    componentDidMount(){
        axios.get('wp-json/wp/v2/articules/18?_embed')
            .then(res => this.setState({
                books: res.data,
                isLoading: true
            }))
            .catch(err => console.log(err))
            console.log(this.state.books)
    }

    render(){
        const { books, isLoading } = this.state;
        if(isLoading) {
        return(
        <div>
            {books.map(books => (
                <ArticuleImageItem key={ books.id} feature={books} />
            ))

            }
        </div>
        )
    }
        return null
    }
}



export default ArticuleImage
