import React, { Component, Fragment } from 'react';
import NarBar from './NavBar';
import axios from 'axios';

class ArticulePage extends Component {
    state = {
        book: {},
        isLoading: false
    }

    componentDidMount(){

        axios.get(`/wp-json/wp/v2/articules/${this.props.match.params.id}`)
            .then( res => {
                this.setState({
                book: res.data,
                isLoading: true
            })
        })
            .catch(err => console.log(err)) 
    }

    componentDidMount(){
        const { featured_media, author } = this.props.feature;
        const getImageURL = axios.get(`/wp-json/wp/v2/media/${featured_media}`)
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`);

        Promise.all([getImageURL, getAuthor]).then(res => {

            this.setState({
                imageURL: res[0].data.media_details.sizes.full.source_url,
                isLoading: true
            })
        })
    }


    render(){
        const { book, isLoading } = this.state;
        if(isLoading){
            return(
                <Fragment>

                    <NarBar />
                    <h1>{book.title.rendered}</h1>
                    <div dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>
                    <h4> Publisher: {book.acf.publisher}</h4>
                </Fragment>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default ArticulePage
