import React, { Component, Fragment } from 'react';
import ArticuleImageItem from './ArticuleImageItem';
import NarBar from './NavBar';
import axios from 'axios';

class ArticulePage extends Component {
    state = {
        book: {},
        isLoading: false,
        imageURL: ''
    }

    componentDidMount(){
        // function getBookInfo() {
        //  return axios.get(`/wp-json/wp/v2/articules/${this.props.match.params.id}`)
        // }
        function getBookImage(){
         const { featured_media } = this.state.book;
         return axios.get(`/wp-json/wp/v2/media/${featured_media}`)
        }
        axios.all( [getBookImage()])
        .then( res => {
            this.setState({
            book: res.data,
            imageURL: res[0].data.media_details.sizes.full.source_url,
            isLoading: true
        })
        console.log(this.state)
    })
        .catch(err => console.log(err)) 
    }



    render(){
        const { book, isLoading, imageURL } = this.state;
        if(isLoading){
            return(
                <Fragment>

                    <NarBar />
                    <h1>{book.title.rendered}</h1>
                    <h1>{imageURL}</h1>
                    <div className='FeaturePage-blurb' dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>
                </Fragment>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default ArticulePage
