import React, { Component} from 'react';
import PropTypes from 'prop-types';
import NavBar from './NavBar';
import axios from 'axios';

class FeaturePage extends Component {
    state = {
        book: {},
        isLoading: false
    }

    static propTypes = {
        feature: PropTypes.object.isRequired
    }

    componentDidMount(){
        axios.get(`/wp-json/wp/v2/feature/${this.props.match.params.id}`)
            .then( res => {
                this.setState({
                book: res.data,
                isLoading: true
            })
        })
            .catch(err => console.log(err)) 
    }




    render(){
        const { book, isLoading } = this.state;
        const { featured_media, title } = this.props.feature;

        if(isLoading){
            return(

                <div>
                    <NavBar />
                    <img className="FeatureBlog-img" src={ featured_media } alt={title.rendered} />
                    <h1>{book.title.rendered}</h1>
                    <div dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>
                    <h4> Publisher: {book.acf.publisher}</h4>
                </div>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default FeaturePage
