import React, { Component } from 'react';
import axios from 'axios';


class LastestBlogs extends Component {
    constructor(props){
        super(props)
        this.state = {
            books: [],
            isLoading: false
        }
    }


    componentDidMount(){
        axios.get('/wp-json/wp/v2/articules')
            .then(res => this.setState({
                books: res.data,
                isLoaded: true
            }))
            .catch(err => console.log(err))

    }

    render(){
      const { books, isLoaded } = this.state;
       if(isLoaded) {
        return (
            <div className="Articule-container">
                { books.map(book => (
                    <ArticuleItems  key={ book.id} book={book} />
                )) }
            </div>
        )
       }

       return <h3>Loading..</h3>
    }
}

export default LastestBlogs
