import React, { Component} from 'react';
import PropTypes from 'prop-types';
import NavBar from './NavBar';
import axios from 'axios';

class FeaturePage extends Component {
    state = {
        book: {},
        imageURL: '',
        isLoading: false
    }

    static propTypes = {
        feature: PropTypes.object.isRequired
    }

    componentDidMount(){

        const getImageURL = axios.get('/wp-json/wp/v2/media/featured_media')
        const getAuthor = axios.get('/wp-json/wp/v2/users/author');

        axios.get(`/wp-json/wp/v2/feature/${this.props.match.params.id}`)
            .then( res => {
                this.setState({
                book: res.data,
                isLoading: true
            })
        })
            .catch(err => console.log(err)) 


        Promise.all([getImageURL, getAuthor]).then(res => {

            this.setState({
                imageURL: res[0].data.media_details.sizes.full.source_url,
                isLoading: true
            })
        })
    }




    render(){
        const { imageURL, book, isLoading } = this.state;
        const { title, excerpt } = this.props;

        if(isLoading){
            return(

                <div>
                    <NavBar />
                    <h1>{title.rendered}</h1>
                    <div dangerouslySetInnerHTML={{ __html: book.content.rendered }}></div>

                </div>
            )
        }

        return <h3>Loading...</h3>

    }
}

export default FeaturePage
